package cry;

import java.math.BigInteger;

import pack.packHolder;

/**
 * diffie-hellman key exchange
 *
 * @author matecsaba
 */
public class cryKeyDH extends cryKeyGeneric {

    /**
     * common value
     */
    public BigInteger common;

    /**
     * generator value
     */
    public BigInteger group;

    /**
     * modulus value
     */
    public BigInteger modulus;

    /**
     * client private value
     */
    protected BigInteger clntPriv;

    /**
     * client public value
     */
    public BigInteger clntPub;

    /**
     * server private value
     */
    protected BigInteger servPriv;

    /**
     * server public value
     */
    public BigInteger servPub;

    public String toString() {
        return "group=" + group + " prime=" + modulus;
    }

    public String algName() {
        return "dh";
    }

    public boolean certReader(packHolder pck) {
        return true;
    }

    public void certWriter(packHolder pck) {
    }

    public boolean privReader(packHolder pck) {
        cryAsn1 a = new cryAsn1();
        if (a.tagRead(pck)) {
            return true;
        }
        if ((!a.cnst) || (a.tag != cryAsn1.tagSequence)) {
            return true;
        }
        pck = a.getPack();
        modulus = cryAsn1.readBigInt(pck);
        if (modulus == null) {
            return true;
        }
        group = cryAsn1.readBigInt(pck);
        if (group == null) {
            return true;
        }
        return false;
    }

    public void privWriter(packHolder pck) {
        packHolder p = new packHolder(true, true);
        cryAsn1.writeBigInt(p, modulus);
        cryAsn1.writeBigInt(p, group);
        cryAsn1.writeSequence(pck, p);
    }

    public void keyMake(int len) {
        for (;;) {
            modulus = randomPrime(len);
            BigInteger i = modulus.shiftRight(1);
            if (testPrime(i)) {
                break;
            }
        }
        group = new BigInteger("2", 16);
    }

    public boolean keyVerify() {
        if (!testPrime(modulus)) {
            return true;
        }
        if (!testPrime(modulus.shiftRight(1))) {
            return true;
        }
        if (!testPrime(group)) {
            return true;
        }
        return false;
    }

    public int keySize() {
        return modulus.bitLength();
    }

    /**
     * get default group
     *
     * @param grp group number
     * @return group, null if none
     */
    public static cryKeyDH getGroup(int grp) {
        cryKeyDH r = new cryKeyDH();
        r.group = new BigInteger("2", 16);
        switch (grp) {
            case 0: // 2^127-1
                r.modulus = new BigInteger("170141183460469231731687303715844105727", 10);
                break;
            case 1:
                r.modulus = new BigInteger("FFFFFFFFFFFFFFFFC90FDAA22168C234C4C6628B80DC1CD129024E088A67CC74020BBEA63B139B22514A08798E3404DDEF9519B3CD3A431B302B0A6DF25F14374FE1356D6D51C245E485B576625E7EC6F44C42E9A63A3620FFFFFFFFFFFFFFFF", 16);
                break;
            case 2:
                r.modulus = new BigInteger("FFFFFFFFFFFFFFFFC90FDAA22168C234C4C6628B80DC1CD129024E088A67CC74020BBEA63B139B22514A08798E3404DDEF9519B3CD3A431B302B0A6DF25F14374FE1356D6D51C245E485B576625E7EC6F44C42E9A637ED6B0BFF5CB6F406B7EDEE386BFB5A899FA5AE9F24117C4B1FE649286651ECE65381FFFFFFFFFFFFFFFF", 16);
                break;
            case 5:
                r.modulus = new BigInteger("", 16);
                break;
            case 14:
                r.modulus = new BigInteger("", 16);
                break;
            case 15:
                r.modulus = new BigInteger("", 16);
                break;
            case 16:
                r.modulus = new BigInteger("", 16);
                break;
            case 17:
                r.modulus = new BigInteger("", 16);
                break;
            case 18:
                r.modulus = new BigInteger("", 16);
                break;
            case 22:
                r.modulus = new BigInteger("B10B8F96A080E01DDE92DE5EAE5D54EC52C99FBCFB06A3C69A6A9DCA52D23B616073E28675A23D189838EF1E2EE652C013ECB4AEA906112324975C3CD49B83BFACCBDD7D90C4BD7098488E9C219A73724EFFD6FAE5644738FAA31A4FF55BCCC0A151AF5F0DC8B4BD45BF37DF365C1A65E68CFDA76D4DA708DF1FB2BC2E4A4371", 16);
                r.group = new BigInteger("A4D1CBD5C3FD34126765A442EFB99905F8104DD258AC507FD6406CFF14266D31266FEA1E5C41564B777E690F5504F213160217B4B01B886A5E91547F9E2749F4D7FBD7D3B9A92EE1909D0D2263F80A76A6A24C087A091F531DBF0A0169B6A28AD662A4D18E73AFA32D779D5918D08BC8858F4DCEF97C2A24855E6EEB22B3B2E5", 16);
                break;
            case 23:
                r.modulus = new BigInteger("", 16);
                r.group = new BigInteger("", 16);
                break;
            case 24:
                r.modulus = new BigInteger("", 16);
                r.group = new BigInteger("", 16);
                break;
            default:
                r = null;
                break;
        }
        return r;
    }

    /**
     * find one group
     *
     * @param siz size in bits
     * @return group, null if none
     */
    public static cryKeyDH findGroup(int siz) {
        int p = -1;
        int o = siz;
        for (int i = 0; i < 1024; i++) {
            cryKeyDH g = getGroup(i);
            if (g == null) {
                continue;
            }
            int q = g.keySize() - siz;
            if (q < 0) {
                q = -q;
            }
            if (q >= o) {
                continue;
            }
            o = q;
            p = i;
        }
        return getGroup(p);
    }

    /**
     * client exchange
     */
    public void clntXchg() {
        clntPriv = randomBigInt(modulus.bitLength() - 2);
        clntPub = group.modPow(clntPriv, modulus);
    }

    /**
     * server exchange
     */
    public void servXchg() {
        servPriv = randomBigInt(modulus.bitLength() - 2);
        servPub = group.modPow(servPriv, modulus);
    }

    /**
     * client common secret computation
     */
    public void clntKey() {
        common = servPub.modPow(clntPriv, modulus);
    }

    /**
     * server common secret computation
     */
    public void servKey() {
        common = clntPub.modPow(servPriv, modulus);
    }

    public boolean sshReader(byte[] key) {
        return true;
    }

    public byte[] sshWriter() {
        return null;
    }

    public boolean sshVerify(byte[] hash, byte[] sign) {
        return true;
    }

    public byte[] sshSigning(byte[] hash) {
        return null;
    }

    public boolean certVerify(byte[] hash, byte[] sign) {
        return true;
    }

    public byte[] certSigning(byte[] hash) {
        return null;
    }

    public boolean tlsVerify(int ver, byte[] hash, byte[] sign) {
        return true;
    }

    public byte[] tlsSigning(int ver, byte[] hash) {
        return null;
    }

}
